// import React, { Component } from "react";
import React, { useState, useEffect, Component } from "react";
import "./style.css";
import API from "../../../utils/API";


// class Card extends Component() {
function Card() {    

        // state = {   transactions:[], 
        //             debt:0, 
        //             credit:0
        // }
        const [transactions, setTransactions] = useState([])
        // const [formObject, setFormObject] = useState({})

        useEffect(() => {
            loadTransactions()
        }, [])

        function loadTransactions(){
            API.getTransactions()
            .then( res =>
                setTransactions(res.data)
           )
           .catch(err => console.log(err));
        };  

        //addAmount is a helper function takes in true/false parameter, depending if you are collecting credit or debt.        
        //if goes through all rows in transaction array and adds the value of all the debt if false, and all the credit if true.
        function addAmount(direction) {
            var amount = 0;
            if(direction){
                transactions.map( transaction => 
                {
                        if(transaction.type = "Credit")
                            amount += transaction.bill;   
                    }        
                );
            }
            else{
                transactions.map(transaction => 
                    {    
                        if(transaction.type = "Debt")
                            amount += transaction.bill;
                    }           
                );
            }
            return amount;           
        }

        function setDebtCredit(){
            this.setState({
                debt: this.addAmount("false"),
                credit: this.addAmount("true")
              });
        }

        // render() {

        //     this.setDebtCredit();
            // let { debt , credit } = this.state

            return (
                <div>
                    <div class="card">
                        <img class="responsive-img" style={{width: 50 , marginLeft:20 ,marginTop:30 }}></img>
                        <h4 class="flow-text">Title</h4>
                        <div class="card-content">
                            <div class="flow-text" class="amount" style="color:green"> ${this.state.credit}</div>

                        </div>
                    </div>

                    <div class="card">
                        <img class="responsive-img" style={{width: 50 , marginLeft:20 ,marginTop:30 }}></img>
                        <h4 class="flow-text">Title</h4>
                        <div class="card-content">
                            <div class="flow-text" class="amount" style="color:red"> -${this.addAmount("debt")}</div>

                        </div>
                    </div>

                </div>
            );
        }    
// }    


export default Card;
