    function renderTable() {
        return (
            <table class="responsive-table">
                <thead>
                    <tr>
                        <th>Location</th>
                        <th>Amount</th>
                        <th>Date</th>
                        <th>Status</th>
                    </tr>
                </thead>

                <tbody>
                    {transactions.map(item =>
                    <tr key={item.id}> 
                        <td>{item.location}</td>
                        <td>${item.bill}</td>
                        <td>{item.date}</td>
                        <td>{item.status}</td>
                    </tr>
                    )}
                </tbody>
            </table>
        );
    }

    return(
        <div class="row">
            <CreditCard>${getCredit()}</CreditCard>
            <DebitCard>- ${getDebt()}</DebitCard>
            <div class="row">
                <MaterialInput placeholder="Name"/>
                <Radio />
            </div>
            <div>
                {renderTable()}
            </div>
        </div>  
    );

}   


export default Dashhboard;
