                </tr>
            </thead>

            <tbody>
                {transactions.map(item =>
                <tr key={item.id}> 
                    <td>{item.location}</td>
                    <td>{item.bill}</td>
                    <td>{item.date}</td>
                    <td>{item.status}</td>
                    {/* <button
