import React, { useState, useEffect } from "react";
// import CardContainer from "../../src/components/CardContainer";
// import ComposeContainer from "../../src/components/ComposeContainer";
// import TransactionTable from "../../src/components/TransactionsTable";
// import Card2 from "../../src/components/newComponents/Card2";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List";
import { Col, Row, Container } from "../components/Grid";




function Dashhboard () {

    const [transactions, setTransactions] = useState([])
    // const [debt , setDebt] = useState(0)
    // const [credit , setCredit] = useState(0)

    useEffect(() => {
        API.getTransactions()
            .then(res => {
                console.log(res)
                setTransactions(res.data)
            })
        // loadTransactions()
    }, [])

    function loadTransactions(){
        API.getTransactions()
        .then( res =>
            setTransactions(res.data)
       )
       .catch(err => console.log(err));
    };  

    return (
        <div>
            <ul>
                {
                    this.transactions.map(transaction => <li key={transaction.id}>{transaction.name}</li>)
                }
            </ul>
        </div>
    );    
}   

export default Dashhboard;
