import React, { useState, useEffect } from "react";
// import CardContainer from "../../src/components/CardContainer";
// import ComposeContainer from "../../src/components/ComposeContainer";
// import TransactionTable from "../../src/components/TransactionsTable";
// import Card2 from "../../src/components/newComponents/Card2";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List";
import { Col, Row, Container } from "../components/Grid";




function Dashhboard () {

    const [transactions, setTransactions] = useState([])

    useEffect(() => {
        API.getTransactions()
        .then( res =>
            setTransactions(res.data)
        )

    }, [])

    function getDebt() {
        let debt = 0;
            transactions.map( transaction => 
                {
                    if(transaction.type = true)
                        debt += transaction.bill;   
                }        
            );
        return debt;
    }
    function getCredit() {
        let credit = 0;
            transactions.map(transaction => 
                {    
                    if(transaction.type = false)
                        credit += transaction.bill;
                }           
            );   
        return credit;
    }

    return (
        <div>
            <ul>

                    {/* transactions.map(transaction => <li key={credit}>{transaction.bill}</li>) */}
                    <li>{getDebt()}</li>
                    <li>{getCredit()}</li>


            </ul>
        </div>
    );    
}   

export default Dashhboard;
